mapper.select:
select
ID, CREATE_BY, CREATE_DATE, LAST_MODIFIED_BY, LAST_MODIFIED_DATE, LAST_MODIFIED_VERSION, ACCOUNT_ID,
ADJUST_INCREASE_MONEY, ADJUST_INCREASING_MONEY, ADJUST_MINUS_MONEY, ADJUST_MINUSING_MONEY, ADM_DIV_CODE,
AVI_MONEY, AVING_MONEY, BALANCE_ID, CAN_USE_MONEY, CCID, DISTRIBUTE_FREEZE_MONEY, DISTRIBUTE_FREEZING_MONEY,
DISTRIBUTE_MINUS_END_MONEY, DISTRIBUTE_MINUSING_MONEY, DISTRIBUTE_MONEY, DISTRIBUTE_UNFREEZE_MONEY,
DISTRIBUTE_UNFREEZING_MONEY, DISTRIBUTING_MONEY, FREEZE_MONEY, FREEZING_MONEY, FROMCTRLID, INCREASE_MONEY,
INCREASING_MONEY, MINUS_END_MONEY, MINUS_MONEY, MINUSING_MONEY, MONTH, RECOVER_MONEY, RECOVERING_MONEY, REMARK,
SECOND_FROMCTRLID, SUM_ID, UNFREEZE_MONEY, UNFREEZING_MONEY, USE_END_MONEY, USE_MONEY, USING_MONEY,
VERIFICATION_MONEY, VERIFICATIONING_MONEY, YEAR
from DFDEVDF0114.FGL_BALANCE


        and ID = #{id}


        and CREATE_BY = #{createBy}


        and CREATE_DATE = #{createDate}


        and LAST_MODIFIED_BY = #{lastModifiedBy}


        and LAST_MODIFIED_DATE = #{lastModifiedDate}


        and LAST_MODIFIED_VERSION = #{lastModifiedVersion}


        and ACCOUNT_ID = #{accountId}


        and ADJUST_INCREASE_MONEY = #{adjustIncreaseMoney}


        and ADJUST_INCREASING_MONEY = #{adjustIncreasingMoney}


        and ADJUST_MINUS_MONEY = #{adjustMinusMoney}


        and ADJUST_MINUSING_MONEY = #{adjustMinusingMoney}


        and ADM_DIV_CODE = #{admDivCode}


        and AVI_MONEY = #{aviMoney}


        and AVING_MONEY = #{avingMoney}


        and BALANCE_ID = #{balanceId}


        and CAN_USE_MONEY = #{canUseMoney}


        and CCID = #{ccid}


        and DISTRIBUTE_FREEZE_MONEY = #{distributeFreezeMoney}


        and DISTRIBUTE_FREEZING_MONEY = #{distributeFreezingMoney}


        and DISTRIBUTE_MINUS_END_MONEY = #{distributeMinusEndMoney}


        and DISTRIBUTE_MINUSING_MONEY = #{distributeMinusingMoney}


        and DISTRIBUTE_MONEY = #{distributeMoney}


        and DISTRIBUTE_UNFREEZE_MONEY = #{distributeUnfreezeMoney}


        and DISTRIBUTE_UNFREEZING_MONEY = #{distributeUnfreezingMoney}


        and DISTRIBUTING_MONEY = #{distributingMoney}


        and FREEZE_MONEY = #{freezeMoney}


        and FREEZING_MONEY = #{freezingMoney}


        and FROMCTRLID = #{fromctrlid}


        and INCREASE_MONEY = #{increaseMoney}


        and INCREASING_MONEY = #{increasingMoney}


        and MINUS_END_MONEY = #{minusEndMoney}


        and MINUS_MONEY = #{minusMoney}


        and MINUSING_MONEY = #{minusingMoney}


        and MONTH = #{month}


        and RECOVER_MONEY = #{recoverMoney}


        and RECOVERING_MONEY = #{recoveringMoney}


        and REMARK = #{remark}


        and SECOND_FROMCTRLID = #{secondFromctrlid}


        and SUM_ID = #{sumId}


        and UNFREEZE_MONEY = #{unfreezeMoney}


        and UNFREEZING_MONEY = #{unfreezingMoney}


        and USE_END_MONEY = #{useEndMoney}


        and USE_MONEY = #{useMoney}


        and USING_MONEY = #{usingMoney}


        and VERIFICATION_MONEY = #{verificationMoney}


        and VERIFICATIONING_MONEY = #{verificationingMoney}


        and YEAR = #{year}
